import React from 'react'
import { IMG_CDN } from '../utils/constants';

const MovieCard = ({posterPath}) => {
  if (!posterPath) return null;
  return (
    <div className='w-60 h-52 md:w-80 pr-4 scrollbar-hide object-fit'>
      <img className='w-80 h-52 object-fill object-center' alt='movie poster' src={IMG_CDN + posterPath} />
    </div>
  )
}

export default MovieCard
