import React, { useEffect, useState } from 'react'
import { OPTIONS } from '../utils/constants';

const VideoBG = () => {
  const [movieKey, setMovieKey] = useState(null);
  const getMovieVideos = async () =>{
    const data = await fetch("https://api.themoviedb.org/3/movie/1022789/videos", OPTIONS);
    const json = await data.json();
    console.log(json);
    const filterData = json.results.filter((video)=>video.type === "Trailer");
    const trailer = filterData.length ? filterData[0] : json.results[0];
    console.log(trailer);
    setMovieKey(trailer.key);
  }
  useEffect(()=>{
    getMovieVideos();
  },[])
  if(movieKey == null) return;
  return (
    <div>
      <iframe width="560" height="315" src={"https://www.youtube.com/embed/"+{movieKey} +"?si=xbDUVrSmWzOC9Pnh"} title="YouTube video player" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerPolicy="strict-origin-when-cross-origin"></iframe>
    </div>
  )
}

export default VideoBG
