import React from 'react'

const VideoTitle = ({movie}) => {
  const {original_title, overview} = movie;
  return (
    <div className='pt-20 px-10 absolute text-white bg-gradient-to-l from-black'>
      <h1 className='mb-1 text-2xl font-bold'>{original_title}</h1>
      <p className='w-3/5'>{overview}</p>
      <div className='flex gap-4 mt-4'>
        <button className='bg-black text-white px-4 font-semibold text-lg rounded-lg'>Play</button>
        <button className='bg-gray-500 text-white px-4 rounded-lg text-lg'>more info</button>
      </div>
    </div>
  )
}

export default VideoTitle
