import React from 'react'
import lang from '../utils/languageConstants'
import { useSelector } from 'react-redux'

const GptSearchBar = () => {
    const selectedLanguage = useSelector((store)=>store.lang.lang);
    console.log(selectedLanguage);
  return (
    <div className='w-screen h-screen bg-gradient-to-b from-zinc-950'>
    <div className='pt-[13%] flex justify-center'>
      <form className='bg-black w-1/2 grid grid-cols-12' onSubmit={(e)=>e.preventDefault()}>
        <input type='text' className='p-2 text-white m-4 col-span-9 outline-none bg-zinc-800' placeholder={lang[selectedLanguage].gptSearchPH}/>
        <button className='px-4 py-1 font-semibold bg-red-500 rounded-sm text-white col-span-3 m-4'>{lang[selectedLanguage].search}</button>
      </form>
    </div>
    </div>
  )
}

export default GptSearchBar
